import React, { useState, useRef, useEffect } from "react";
import axios from 'axios';
import './App.css';
import './css/homePage.css'


const App = () => {
  const [inputText, setInputText] = useState("");
  const [currentImage, setCurrentImage] = useState(0);
  const [storyText, setStoryText] = useState([]);
  const [isAnimating, setIsAnimating] = useState(false);
  const [audioSrc, setAudioSrc] = useState([]); // Store decoded audio URL
  const [isRecording, setIsRecording] = useState(false); // Track if the mic is recording
  const mediaRecorderRef = useRef(null);
  const [isHomeVisible, setHomeVisible] = (false)
  const chunks = useRef([]); // To store audio data


  const images = [
    '/images/7.png',
    '/images/8.png',
    '/images/9.png',
  ];

  useEffect(() => {
    // Set up the interval to switch the image every 5 seconds (5000 ms)
    const intervalId = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 5000);

    // Clear the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, [images.length]); 

  const BASE_API = "http://localhost:3000"


  const handleNext = () => {
    if (!isAnimating) {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentImage((prev) => (prev === images.length - 1 ? 0 : prev + 1));
        setIsAnimating(false);
      }, 500); // Duration of the animation
    }
  };

  const handlePrevious = () => {
    if (!isAnimating) {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentImage((prev) => (prev === 0 ? images.length - 1 : prev - 1));
        setIsAnimating(false);
      }, 500); // Duration of the animation
    }
  };

  const handleRecord = () => {
    if (isRecording) {
      mediaRecorderRef.current.stop(); // Stop recording
      setIsRecording(false);
    } else {
      // Request audio access from the user
      navigator.mediaDevices.getUserMedia({ audio: true }).then((stream) => {
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        chunks.current = [];

        // Store the data in chunks
        mediaRecorder.ondataavailable = (e) => {
          chunks.current.push(e.data);
        };

        // When the recording stops
        mediaRecorder.onstop = () => {
          const blob = new Blob(chunks.current, { type: "audio/wav" });
          // Send the audio blob to the API
          sendAudioToAPI(blob);
        };

        mediaRecorder.start();
        setIsRecording(true); // Update state to show that we are recording
      });
    }
  };

  const convertBlobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      var reader = new FileReader();
      reader.onloadend = () => {
        const base64String = reader.result.split(',')[1]; // Extract Base64 string without MIME type
        resolve(base64String);
      };
      reader.onerror = (error) => reject(error);
      reader.readAsDataURL(blob);
    });
  };

  const decodeBase64Audio = (base64String) => {
    if (base64String) {
      const binaryString = atob(base64String);
      const binaryLen = binaryString.length;
      const bytes = new Uint8Array(binaryLen);

      for (let i = 0; i < binaryLen; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }


      const blob = new Blob([bytes], { type: 'audio/wav' }); 


      const url = URL.createObjectURL(blob);
      setAudioSrc([...audioSrc, url]); 
    }
  };

  const submitRequest = () => {
    axios.post(BASE_API+"/gemini", {"message": inputText}).then((resGemi) => {
      setStoryText(resGemi.data);
      axios.post(BASE_API+"/lmnt", {"message" : resGemi.data}).then((resLMNT) => {
        for (let index = 0; index < resLMNT.data.length; index++) {
          decodeBase64Audio(resLMNT.data[index]);
        } 
        const audio = new Audio(audioSrc);
        audio.play(); 
        alert("Story Generated");

      }).catch(e => alert(e));

    });

    setAudioSrc([])
  };
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const handleSend = () => {
    if (input.trim()) {
      // Add the user's message
      const newMessage = { text: input, sender: 'user' };
      setMessages((prevMessages) => [...prevMessages, newMessage]);

      // Here you can call your API to get a response from ChatGPT
      // Simulating a response from ChatGPT for demonstration purposes
      const botResponse = { text: `You said: ${input}`, sender: 'bot' };
      setMessages((prevMessages) => [...prevMessages, botResponse]);

      // Clear the input field
      setInput('');
    }
  };
  // Function to send the recorded audio blob to an API endpoint
  const sendAudioToAPI = (audioBlob) => {
    convertBlobToBase64(audioBlob).then((base64) => {
      axios.post(BASE_API+"/deepgram", {
        "message": base64
      }).then((res) => {
        axios.post(BASE_API+"/gemini", {"message": res.data}).then((resGemi) => {
          setStoryText(resGemi.data);
          axios.post(BASE_API+"/lmnt", {"message" : resGemi.data}).then((resLMNT) => {
            decodeBase64Audio(resLMNT.data[0]); // Decode Base64 audio from response
            alert("Story Generated");
          }).catch(e => alert(e));
        });
      });
    });
  };

  return (
    <div className="chat-container">
    <h1>ChatGPT-like Dashboard</h1>
    <div className="messages">
      {messages.map((msg, index) => (
        <div key={index} className={msg.sender}>
          <strong>{msg.sender === 'user' ? 'You' : 'ChatGPT'}:</strong> {msg.text}
        </div>
      ))}
    </div>
    <div className="input-container">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && handleSend()} // Send message on Enter key
        placeholder="Type a message..."
      />
      <button onClick={handleSend}>Send</button>
    </div>
  </div>
  );
};

export default App;




