import React, { useState, useRef } from "react";
import axios from 'axios';
import './App.css';

const App = () => {
  const [inputText, setInputText] = useState("");
  const [currentImage, setCurrentImage] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [animationDirection, setAnimationDirection] = useState(''); // Track animation direction ('left' or 'right')
  const [isRecording, setIsRecording] = useState(false); // Track if the mic is recording
  const mediaRecorderRef = useRef(null);
  const chunks = useRef([]); // To store audio data

  const BASE_API = "http://localhost:3000"

  const images = [
    '/images/7.png',
    '/images/8.png',
    '/images/9.png',
  ];

  const handleNext = () => {
    if (!isAnimating) {
      setAnimationDirection('left');
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentImage((prev) => (prev === images.length - 1 ? 0 : prev + 1));
        setIsAnimating(false);
      }, 500); // Duration of the animation
    }
  };

  const handlePrevious = () => {
    if (!isAnimating) {
      setAnimationDirection('right');
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentImage((prev) => (prev === 0 ? images.length - 1 : prev - 1));
        setIsAnimating(false);
      }, 500); // Duration of the animation
    }
  };

  const handleRecord = () => {
    if (isRecording) {
      mediaRecorderRef.current.stop(); // Stop recording
      setIsRecording(false);
    } else {
      // Request audio access from the user
      navigator.mediaDevices.getUserMedia({ audio: true }).then((stream) => {
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        chunks.current = [];

        // Store the data in chunks
        mediaRecorder.ondataavailable = (e) => {
          chunks.current.push(e.data);
        };

        // When the recording stops
        mediaRecorder.onstop = () => {

          const blob = new Blob(chunks.current, { type: "audio/wav" });
          // Send the audio blob to the API
          sendAudioToAPI(blob);
        };

        mediaRecorder.start();
        setIsRecording(true); // Update state to show that we are recording
      });
    }
  };

  const convertBlobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      console.log("inside promise");
      var reader = new FileReader();
      reader.onloadend = () => {
        console.log("onloadend")
        const base64String = reader.result.split(',')[1]; // Extract Base64 string without MIME type
        console.log("base64 ", base64String)
        resolve(base64String);
      };
      reader.onerror = (error) => {
        console.error('FileReader Error:', error); // Log the error
        reject(error);
      };
      try {
        reader.readAsDataURL(blob); // Attempt to read the Blob
        console.log("ojgjrguew")
      } catch (error) {
        console.error('Error occurred during Blob conversion:', error);
        reject(error);
      }
    });
  }

  const submitRequest = () => {
      axios.post(BASE_API+"/gemini", {"message": inputText}).then((resGemi) => {
        axios.post(BASE_API+"/lmnt", {"message" : resGemi.data}).then((resLMNT) => {
          alert("Story Generated")
        }).catch(e => alert(e))
      })
  }

  // Function to send the recorded audio blob to an API endpoint
  const sendAudioToAPI = (audioBlob) => {
    convertBlobToBase64(audioBlob).then((base64) => {
      axios.post(BASE_API+"/deepgram", {
        "message": base64
      }).then((res) => {
        axios.post(BASE_API+"/gemini", {"message": res.data}).then((resGemi) => {
          axios.post(BASE_API+"/lmnt", {"message" : resGemi.data}).then((resLMNT) => {
            alert("Story Generated")
          }).catch(e => alert(e))
        })
      });


      // fetch('', {
      //   method: 'POST',
      //   body: ,
      // })
      // .then((response) => response.json())
      // .then((data) => {
      //   console.log('Success:', data); // You can handle the API response here
      // })
      // .catch((error) => {
      //   console.error('Error uploading audio:', error); // Handle the error here
      // });

    });
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Welcome to Story</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Input text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
          />
          <button className="mic-button" onClick={handleRecord}>
            {isRecording ? '🎙️' : '🎤'} {/* Show different icon when recording */}
          </button>
          <button onClick={submitRequest}>Submit</button>
        </div>
      </header>
      <main className="app-main">
        <div className="image-display-box">
          <div className={`image-display ${isAnimating ? animationDirection : ''}`}>
            <img
              className={`image ${isAnimating ? `slide-${animationDirection}` : ''}`}
              src={images[currentImage]}
              alt="Carousel"
            />
          </div>
          <button className="prev-button" onClick={handlePrevious}>
            &#10094;
          </button>
          <button className="next-button" onClick={handleNext}>
            &#10095;
          </button>
        </div>
      </main>
    </div>
  );
};

export default App;
